con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Hello, my first comment"

//Context variable is used to access and modify the properties of your testcase
//Scope of Context lies in TestCase only
log.info context.expand('${#TestCase#status}')
log.info context.expand('${#TestCase#age}')

//TestRunner: To access any property we have to come through its parent only
log.info testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("id")

// Project > TestSuite > TestCases > TestStep
// Access custom properties at Project level
log.info testRunner.testCase.testSuite.project.getPropertyValue("name")

//Access custom propertes at testCase level
log.info testRunner.testCase.getPropertyValue("status")
log.info testRunner.testCase.testSuite.testCases["Get Employee"].getPropertyValue("department")

//Access custom propeties at testSuite level
log.info testRunner.testCase.testSuite.getPropertyValue("gender")

//Set value for these custom Properties
testRunner.testCase.testSuite.project.setPropertyValue("name", "Gauri")
testRunner.testCase.testSuite.setPropertyValue("gender","F")
testRunner.testCase.testSuite.testCases["AddEmployee"].setPropertyValue("id", "38")
testRunner.testCase.testSuite.testCases["Get Employee"].setPropertyValue("department", "IT")
testRunner.testCase.setPropertyValue("age","27")

//Fetch the request body of AddEmployee
log.info testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"].getPropertyValue("Request")

//Hit AddEmployee Request with properties from TestCase level 
def addRequest = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"].getPropertyValue("Request")
def name = testRunner.testCase.testSuite.project.getPropertyValue("name")
def id = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("id")
def department = testRunner.testCase.testSuite.testCases["Get Employee"].getPropertyValue("department")
def age = testRunner.testCase.getPropertyValue("age")

//Use XmlHolder for parsing the the xml request
def xmlAddReq = new XmlHolder(addRequest)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:name", name)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:id", id)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:Department", department)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:age", age)

def updatedXml = xmlAddReq.getXml()
testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"].setPropertyValue("Request", updatedXml)
log.info updatedXml

//Call the service
//context of AddEmployee should be passed as Add Step which we are trying to run belongs to that TestCase
def addTestStep = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"]
def addEmployeeContext = new WsdlTestRunContext(addTestStep)
addTestStep.run(testRunner, addEmployeeContext)

//Hit GetEmployee and generate Response. Validate the response if it have the name which we added earlier
def getRequest = testRunner.testCase.testSuite.testCases["Get Employee"].testSteps["get"].getPropertyValue("Request")
def xmlGetReq = new XmlHolder(getRequest)
//def getName = xmlAddReq.getNodeValue("//typ:addEmployee/typ:name")

xmlGetReq.setNodeValue("//typ:getEmployeeDetails/typ:employeeName", name)
def updatedGetRequestXml = xmlGetReq.getXml()
testRunner.testCase.testSuite.testCases["Get Employee"].testSteps["get"].setPropertyValue("Request", updatedGetRequestXml)

def getTestStep = testRunner.testCase.testSuite.testCases["Get Employee"].testSteps["get"]
def getTestStepContext = new WsdlTestRunContext(getTestStep)
getTestStep.run(testRunner, getTestStepContext)

//Validation
def getResponse = testRunner.testCase.testSuite.testCases["Get Employee"].testSteps["get"].getPropertyValue("Response")
def xmlGetRes = new XmlHolder(getResponse)
def getResponseName = xmlGetRes.getNodeValue("//ns:getEmployeeDetailsResponse/ns:return/ns:name")

log.info getResponseName
log.info name
assert getResponseName == name

//Hit Delete Employee
def deleteRequest = testRunner.testCase.testSuite.testCases["DeleteEmployee"].testSteps["delete"].getPropertyValue("Request")
def xmlDelete = new XmlHolder(deleteRequest)
xmlDelete.setNodeValue("//typ:deleteEmployee/typ:employeeName", name)
def updatedDeleteXml = xmlDelete.getXml()
testRunner.testCase.testSuite.testCases["DeleteEmployee"].testSteps["delete"].setPropertyValue("Request", updatedDeleteXml)

def deleteTestStep = testRunner.testCase.testSuite.testCases["DeleteEmployee"].testSteps["delete"]
def deleteTestStepContext = new WsdlTestRunContext(deleteTestStep)
deleteTestStep.run(testRunner,deleteTestStepContext)
